                    app.log.debug("下载app成功："+ d.filename);
                    plus.nativeUI.showWaiting("安装app文件...");
                    plus.runtime.install(d.filename, {} ,function(){
                        plus.nativeUI.closeWaiting();
                        app.log.debug("安装app文件成功！");
