                        plus.nativeUI.alert("安装app文件失败["+e.code+"]："+e.message);
                    });
                } else {
                    app.log.debug("下载wgt失败！");
                    plus.nativeUI.alert("下载wgt失败！");
                }
                plus.nativeUI.closeWaiting();
            }).start();
        } else if(app.Config.device.isIOS){
